import React, { useState, useEffect } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";

interface Labor {
    id: number;
    name: string;
    skills: string;
    mobile: string;
    address: string;
    city: string;
}

const Home: React.FC = () => {
    const [laborList, setLaborList] = useState<Labor[]>([]);
    const [searchQuery, setSearchQuery] = useState("");

    const [skillSearch, setSkillSearch] = useState<string>("");
    const [citySearch, setCitySearch] = useState<string>("");

    useEffect(() => {
        const fetchLabors = async () => {
            try {
                const response = await fetch("http://localhost:5000/api/labors"); // Adjust the URL if necessary
                if (response.ok) {
                    const data = await response.json();
                    setLaborList(data);
                } else {
                    console.error("Failed to fetch labors:", response.statusText);
                }
            } catch (error) {
                console.error("Error fetching labors:", error);
            }
        };

        fetchLabors();
    }, []);

    const handleLaborRegister = async (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();

        const formData = new FormData(event.currentTarget);
        const newLabor = {
            name: formData.get("name") as string,
            skills: formData.get("skills") as string,
            mobile: formData.get("mobile") as string,
            address: formData.get("address") as string,
            city: formData.get("city") as string,
        };

        try {
            const response = await fetch("http://localhost:5000/api/labors", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(newLabor),
            });

            if (response.ok) {
                const savedLabor = await response.json();
                setLaborList((prev) => [...prev, savedLabor]); // Update the labor list with the new entry
            }
        } catch (error) {
            console.error("Failed to add labor:", error);
        }
    };


    const filteredLabor = laborList.filter((labor) => {
        return (
            labor.skills.toLowerCase().includes(skillSearch.toLowerCase()) &&
            labor.city.toLowerCase().includes(citySearch.toLowerCase())
        );
    });

    return (
        <>
            <Header />
            <main className="bg-gray-100 min-h-screen">
                <div className="container mx-auto p-6">
                    {/* Two Sections */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                        {/* Section 1: Customer Search */}
                        <section className="bg-white shadow-md rounded-lg p-6">
                            <h2 className="text-2xl font-bold mb-4 text-blue-600">Search for Worker</h2>
                            <form>
                                <div className="mb-4">
                                    <label htmlFor="citySearch" className="block text-gray-700 font-medium mb-2">
                                        Enter City:
                                    </label>
                                    <input
                                        type="text"
                                        id="citySearch"
                                        placeholder="E.g., Delhi, Mumbai"
                                        value={citySearch}
                                        onChange={(e) => setCitySearch(e.target.value)}
                                        className="w-full border rounded px-4 py-2"
                                    />
                                </div>
                                <div className="mb-4">
                                    <label htmlFor="skillSearch" className="block text-gray-700 font-medium mb-2">
                                        Enter Skill:
                                    </label>
                                    <input
                                        type="text"
                                        id="skillSearch"
                                        placeholder="E.g., Plumber, Electrician"
                                        value={skillSearch}
                                        onChange={(e) => setSkillSearch(e.target.value)}
                                        className="w-full border rounded px-4 py-2"
                                    />
                                </div>

                            </form>

                            {/* Recently Added Labor List */}
                            <div className="mt-6">
                                <h3 className="text-lg font-semibold text-gray-800 mb-2">Recently Added Worker:</h3>
                                <div
                                    className="max-h-64 overflow-y-auto border rounded-lg p-2 bg-gray-50"
                                    style={{ scrollbarWidth: "thin" }}
                                >
                                    {filteredLabor.length > 0 ? (
                                        <ul>
                                            {filteredLabor.map((labor) => (
                                                <li
                                                    key={labor.id}
                                                    className="border-b py-2 text-gray-700 flex justify-between"
                                                >
                                                    <span>
                                                        <strong>{labor.name}</strong> ({labor.skills}, {labor.city})
                                                    </span>
                                                    <span className="text-sm text-gray-500">{labor.mobile}</span>
                                                </li>
                                            ))}
                                        </ul>
                                    ) : (
                                        <p className="text-gray-500">No labor found for the given skill and city.</p>
                                    )}
                                </div>
                            </div>
                        </section>

                        {/* Section 2: Labor Registration */}
                        <section className="bg-white shadow-md rounded-lg p-6">
                            <h2 className="text-2xl font-bold mb-4 text-green-600">Register as a Worker</h2>
                            <form onSubmit={handleLaborRegister}>
                                <div className="mb-4">
                                    <label htmlFor="name" className="block text-gray-700 font-medium mb-2">
                                        Full Name:
                                    </label>
                                    <input
                                        type="text"
                                        id="name"
                                        name="name"
                                        placeholder="Enter your full name"
                                        className="w-full border rounded px-4 py-2"
                                        required
                                    />
                                </div>

                                <div className="mb-4">
                                    <label htmlFor="skills" className="block text-gray-700 font-medium mb-2">
                                        Skills (e.g., Plumber, Electrician):
                                    </label>
                                    <input
                                        type="text"
                                        id="skills"
                                        name="skills"
                                        placeholder="Enter your skills"
                                        className="w-full border rounded px-4 py-2"
                                        required
                                    />
                                </div>

                                <div className="mb-4">
                                    <label htmlFor="mobile" className="block text-gray-700 font-medium mb-2">
                                        Mobile Number:
                                    </label>
                                    <input
                                        type="tel"
                                        id="mobile"
                                        name="mobile"
                                        placeholder="Enter your mobile number"
                                        className="w-full border rounded px-4 py-2"
                                        required
                                    />
                                </div>

                                <div className="mb-4">
                                    <label htmlFor="city" className="block text-gray-700 font-medium mb-2">
                                        City:
                                    </label>
                                    <input
                                        type="text"
                                        id="city"
                                        name="city"
                                        placeholder="Enter your city"
                                        className="w-full border rounded px-4 py-2"
                                        required
                                    />
                                </div>

                                <button
                                    type="submit"
                                    className="bg-green-600 text-white w-full py-2 rounded hover:bg-green-700"
                                >
                                    Register
                                </button>
                            </form>
                        </section>
                    </div>
                </div>
            </main>
            <Footer />
        </>
    );
};

export default Home;
