import React, { useEffect } from "react";

const TranslateButton: React.FC = () => {
    useEffect(() => {
        const script = document.createElement("script");
        script.src = "//translate.google.com/translate_a/element.js?cb=googleTranslateElementInit";
        script.async = true;
        document.body.appendChild(script);

        window.googleTranslateElementInit = function () {
            new window.google.translate.TranslateElement(
                { pageLanguage: "en" },
                "google_translate_element"
            );
        };
    }, []);

    return (
        <div>
            <div id="google_translate_element"></div>
            <button
                onClick={() => {
                    const selectElement =
                        document.querySelector(".goog-te-combo") as HTMLSelectElement;
                    if (selectElement) {
                        selectElement.value = "hi"; // 'hi' is the language code for Hindi
                        selectElement.dispatchEvent(new Event("change"));
                    }
                }}
                className="bg-blue-500 text-white px-4 py-2 rounded"
            >
                Translate to Hindi
            </button>
        </div>
    );
};


const Header: React.FC = () => {
    return (
        <header className="bg-blue-600 text-white p-4">
            <div className="container mx-auto flex justify-between items-center">
                <h1 className="text-2xl font-bold">FindWorker</h1>
                <nav>
                    <ul className="flex space-x-4">
                        <li><a href="/" className="hover:underline">Home</a></li>
                        <li><a href="/search" className="hover:underline">Search</a></li>
                        <li><a href="/profile" className="hover:underline">Profile</a></li>
                    </ul>
                </nav>
            </div>
        </header>
    );
};

export default Header;


export default TranslateButton;
